-- =====================================================
-- USER CONTEXT FUNCTION
-- =====================================================

-- From: create-user-context-function.sql
CREATE OR REPLACE FUNCTION get_user_context(user_email TEXT)
RETURNS TABLE (
  user_id UUID,
  user_name TEXT,
  user_role TEXT,
  restaurant_id UUID,
  restaurant_name TEXT
) AS $$
BEGIN
    RETURN QUERY
    SELECT 
        u.id as user_id,
        u.name as user_name,
        u.role as user_role,
        r.id as restaurant_id,
        r.name as restaurant_name
    FROM users u
    LEFT JOIN restaurants r ON u.id = r.owner_id
    WHERE u.email = user_email;
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

-- =====================================================
-- DELETE RESTAURANT PROCEDURE
-- =====================================================

-- From: delete-restaurant-procedure.sql
CREATE OR REPLACE FUNCTION delete_restaurant(restaurant_uuid UUID)
RETURNS BOOLEAN AS $$
DECLARE
    restaurant_exists BOOLEAN;
BEGIN
    -- Check if restaurant exists
    SELECT EXISTS(SELECT 1 FROM restaurants WHERE id = restaurant_uuid) INTO restaurant_exists;
    
    IF NOT restaurant_exists THEN
        RAISE EXCEPTION 'Restaurant with ID % does not exist', restaurant_uuid;
    END IF;
    
    -- Delete the restaurant (cascade will handle related records)
    DELETE FROM restaurants WHERE id = restaurant_uuid;
    
    RETURN TRUE;
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

