-- From: roles-access-permissions.sql
-- Create roles table for better permission management
CREATE TABLE IF NOT EXISTS roles (
    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    name TEXT UNIQUE NOT NULL,
    description TEXT,
    permissions JSONB,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP
);

-- Insert default roles
INSERT INTO roles (name, description, permissions) VALUES
    ('admin', 'Full system access', '{"all": true}'),
    ('manager', 'Restaurant management access', '{"restaurants": ["read", "write"], "menus": ["read", "write"], "orders": ["read", "write"], "inventory": ["read", "write"]}'),
    ('cashier', 'Point of sale access', '{"orders": ["read", "write"], "menus": ["read"]}'),
    ('chef', 'Kitchen access', '{"orders": ["read"], "menus": ["read"]}'),
    ('waiter', 'Service staff access', '{"orders": ["read", "write"], "menus": ["read"], "reservations": ["read", "write"]}'),
    ('host', 'Host access', '{"reservations": ["read", "write"], "menus": ["read"]}')
ON CONFLICT (name) DO NOTHING;

